import './App.css';
import React from 'react'
import Navigation from "./components/Navigation";
import HorizontalDivider from "./components/HorizontalDivider";
import Footer from "./components/Footer";
import MenuContainer from "./components/MenuContainer";
import AllSectionsContainer from "./components/AllSectionsContainer";
import PageTitle from "./components/PageTitle";
import Timer from "./components/Timer";
import Counter from "./components/Counter";


const navItems = [
        {
            // always have id to use on keys
            id: 0,
            icon: "fa-solid fa-house",
            link: "#",
            linkText: "Home"
        },
        {
            id: 1,
            icon: "fa-solid fa-bars",
            link: "#",
            linkText: "Menu"
        }
    ]


const menuItems = [
    {
        id: 0,
        icon: "fa-solid fa-angles-left",
        link: "#",
        linkText: "Back"
    },
    {
        id: 1,
        icon: "fa-solid fa-landmark-flag",
        link: "#",
        linkText: "Test"
    }
]


function App() {
    // implement delete o buttons
    const [newMenuItems, setMenuItems] = React.useState(menuItems);

    const onButtonClick = (id) => {
        setMenuItems(state => state.filter(x => x.id !== id))
    }
    // -------------------------------------------------------------

    return (
        <div className="App">
            <header className="header">
                <Navigation navItems={navItems}/>
                <PageTitle title="Main Menu"/>
            </header>
            <main>
                <div className="total-container">
                    <MenuContainer
                        newMenuItems={newMenuItems}   // pass items
                        onButtonClick={onButtonClick}/>
                    <HorizontalDivider/>
                    <AllSectionsContainer/>
                </div>
            </main>
            <Footer content="&copy; 2023"/>

            <Timer start={0}/>
            <br/>

            {/* events */}
            <Counter canReset/>
        </div>
    );
}

export default App;
