import React from 'react'

export default function Timer(props) {

    // -------------------------------------------------------
    const [
        seconds,        // stateResult[0] = state value
        setSeconds      // stateResult[1] = function to update state value
    ] = React.useState(props.start);

    setTimeout(() => {
        setSeconds(seconds + 1);
    }, 1000)
    // -------------------------------------------------------

    return (
        <div>
            Time: {seconds}
        </div>
    )
}
